mapper.select:
SELECT boardidx, title, contents, postdate, id, bname, visitcount, likecount, ltrim(to_char(price,'999,999,999')) price, product_state, chatcount, three_dimens, product_tag, woopay, latitude, longitude FROM ( SELECT Tb.*, rownum rNum FROM (SELECT * FROM woo_board WHERE 1=1
AND ${searchField} LIKE '%'||#{searchTxt}||'%'
AND bname in
#{item}
order by boardidx DESC ) Tb) WHERE rNum BETWEEN #{start} AND #{end}

mapper.update:
 	UPDATE woo_file SET 
	 	 del_yn = 'Y'
 	 WHERE fileidx = #{fileidx}
