mapper.select:
  	SELECT * FROM (
	  			SELECT Tb.*, rownum rNum FROM(
	  				SELECT * FROM WOO_REVIEW
	  				WHERE buyer_id = #{user_id} 
	  		ORDER BY reviewidx DESC) TB) WHERE rNum BETWEEN #{start} AND #{end}

  	SELECT * FROM (
	  			SELECT Tb.*, rownum rNum FROM(
	  				SELECT * FROM WOO_REVIEW
	  				WHERE seller_id = #{seller_id} 
	  		ORDER BY reviewidx DESC) TB) WHERE rNum BETWEEN #{start} AND #{end}

  		SELECT COUNT(*) from WOO_BOARD 	
  		WHERE boardidx in
  		 	
 			#{item}
