mapper.update:
UPDATE woo_board set 
	deal_type = '구매자결제완료',
	BUYER_ID = #{param2}
where boardidx = #{param1}

mapper.select:
SELECT PAY_IDX, MERCHANT_UID, PRODUCT_NAME, PRODUCT_PRICE, PAYDATE, BOARD_IDX, BUYER_ID, SELLER_ID, STATUS, PAY_METHOD FROM ( SELECT Tb.*, rownum rNum FROM ( SELECT * FROM woo_pay WHERE 1=1
AND ${searchField} like '%'||#{searchTxt}||'%'
ORDER BY PAY_IDX DESC ) Tb ) WHERE rNum BETWEEN #{start} AND #{end}
